item.desctext:
特にライブラリは必要なく、IXMLHTTPRequestで簡単に出来ます。
// はてな被ブックマーク合計数取得APIの場合
LPCWSTR pszTotal =
L&rdquo;&lt;?xml version=\&ldquo;1.0\&ldquo;?&gt;&rdquo;
L&rdquo;&rdquo;
L&rdquo;bookmark.getTotalCount&rdquo;
L&rdquo;&rdquo;
L&rdquo;&rdquo;
L&rdquo;http://d.hatena.ne.jp/hatenadiary/&rdquo;
L&rdquo;&rdquo;
L&rdquo;&rdquo;
L&rdquo;&rdquo;;
// call
CComPtrMSXML2::IXMLHTTPRequest pReq;
HRESULT hr = pReq.CoCreateInstance(MSXML2::CLSID_XMLHTTP);
hr = pReq-&gt;open(
&nbsp;CComBSTR(L&rdquo;POST&rdquo;),
&nbsp;CComBSTR(L&rdquo;http://b.hatena.ne.jp/xmlrpc &ldquo;),
&nbsp;CComVariant(false),
&nbsp;CComVariant(),
&nbsp;CComVariant());
hr = pReq-&gt;send(CComVariant(pszTotal));
CComBSTR txt;
hr = pReq-&gt;get_responseText(&amp;txt);
cout &lt;&lt; CW2A(txt) &lt;&lt; endl;
